+        int maxPosition = 0;
+        for (Car car : cars) {
+            maxPosition = getComparePosition(maxPosition, car.getPosition());
+        }
+        return maxPosition;
+    }
+
+    private static int getComparePosition(int currentMax, int newPosition) {
+        return Math.max(currentMax, newPosition);
+    }
+    private static List<Integer> findWinners(List<Car> cars, int maxPosition) {
+        List<Integer> winners = new ArrayList<>();
+        for (Car car : cars) {
+            addWinner(winners, car, maxPosition);
+
+        }
+        return winners;
+    }
+    private static void addWinner(List<Integer> winners, Car car, int maxPosition) {
+        if (isWinner(car, maxPosition)) {
+            winners.add(car.getCarNum());
+        }
+    }
+    private static boolean isWinner(Car car, int maxPosition) {
+        return car.getPosition() == maxPosition;
+    }

