+
+        Set<LottoNumber> numbers = poolNumbers.subList(0, Lotto.LOTTO_SIZE)
+                .stream()
+                .map(LottoNumber::new)
+                .collect(Collectors.toSet());
+
+        return new Lotto(numbers);
